sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var tipoAuditoria = g_form.getValue('tipo_auditoria');
	var numinv = g_form.getValue('numero_invitados');
	var numvis = g_form.getValue('numero_viistante');
	var maxPresencial=3;
	var maxVirtual=10;
	var created= g_form.getValue('sys_created_on');

	if(newValue != oldValue){
		g_form.clearOptions('numero_invitados');
	}
		if (tipoAuditoria == 'Presencial') {
		var invRestantes = maxPresencial - parseInt(numvis);
		for(var i=0; i<=invRestantes; i++){
			g_form.addOption('numero_invitados', i.toString(), i.toString());
		}
	}
	if (tipoAuditoria == 'Virtual') {
		var invRestantesV = maxVirtual - parseInt(numvis);
		if (invRestantesV > 0) {
			for(var j = 0; j <= invRestantesV; j ++){
				g_form.addOption('numero_invitados', j.toString(), j.toString());
			}
		}
	}
	// If the form is loading and it is not a new record, set the u_what_needed value to the
		// value from the record before it was loaded
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('numero_invitados', numinv);
			g_form.setValue('numero_viistante', numvis);
		}
	}
