sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
	var sections = g_form.getSections();
	clearSections();
	return;   
   }
	function clearSections() {
		for (var k = 1; k<=4; k++) {
			g_form.setSectionDisplay('visitante_' + k.toString(), false);
			//g_form.setSectionDisplay('invitado_' + k.toString(), false);
		}
	}
	clearSections();	
	var numVisitantes = parseInt(g_form.getValue('numero_viistante' ));
	if (numVisitantes > 0) {
		for (var i = 1; i<=numVisitantes; i++) {
			g_form.setSectionDisplay('visitante_' + i.toString(), true);
		}
	}
	if(isLoading && !g_form.isNewRecord()){
			//g_form.setValue('numero_invitados', numinv);
			g_form.setValue('numero_viistante', numVisitantes);
		}
}

