sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
		if ( newValue == '') {
			return ;
		}

	var numinv = g_form.getValue('numero_invitados');
	var numvis = g_form.getValue('numero_viistante');
	var maxPresencial=3;
	var maxVirtual=10;

		// Clear all of the choices from the What needed field choice list
		g_form.clearOptions('numero_invitados');
	g_form.clearOptions('numero_viistante');

		// If the value of the Request type field is hr, add
		// two hr choices and other to the What needed field choice list
		if(newValue == 'Presencial'){
			for(var i=0; i<=maxPresencial;i++){
				g_form.addOption('numero_invitados',i.toString(), i.toString());
				g_form.addOption('numero_viistante',i.toString(), i.toString());
			}
		}
if(newValue == 'Virtual'){
			for(var j=0; j<=maxVirtual;j++){
				g_form.addOption('numero_invitados',j.toString(), j.toString());
				g_form.addOption('numero_viistante',j.toString(), j.toString());
			}
		}


		// If the form is loading and it is not a new record, set the u_what_needed value to the
		// value from the record before it was loaded
		if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('numero_invitados', numinv);
		g_form.setValue('numero_viistante', numvis);
		}
	}
