sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
	//var sections = g_form.getSections();

	return;   
   }
		if(newValue != oldValue){
		clearSections();
	}

	function clearSections() {
		for (var k = 1; k<=3; k++) {
			g_form.setSectionDisplay('invitado_' + k.toString(), false);


		}

	}
	clearSections();	
	var numInvitados = parseInt(g_form.getValue('numero_invitados' ));
	if (numInvitados > 0) {
		for (var i = 1; i<=numInvitados; i++) {
			g_form.setSectionDisplay('invitado_' + i.toString(), true);
		}
	}
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('numero_invitados', numInvitados);
		}
}

