sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
	var numVisitantes= g_form.getValue('numero_viistante');
	var numInvitados= g_form.getValue('numero_invitados');
	var tipoSolicitud= g_form.getValue('tipo_solicitud');
   if (tipoSolicitud=='Auditoria'&& numVisitantes ==0 || numInvitados ==0){
	g_form.addErrorMessage("Por favor registre al menos un visitante o invitado para la visita");
           return false;
       }
}
